import 'package:flutter/material.dart';
import 'package:cloud_firestore/cloud_firestore.dart';
import 'package:carousel_slider/carousel_slider.dart';

class HomeScreen extends StatefulWidget {
  const HomeScreen({super.key});

  @override
  State<HomeScreen> createState() => _HomeScreenState();
}

class _HomeScreenState extends State<HomeScreen> {

  List colors = [Colors.red, Colors.orange, Colors.green, Colors.black];
  List images = ["data:image/jpeg;base64,",
    "data:image/jpeg;base64,",
    "data:image/jpeg;base64,",
    "data:image/jpeg;base64,"
  ];

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: Text("ProStore.",style: TextStyle(color: Colors.black),),
        centerTitle: true,
        backgroundColor: Colors.white,
      ) ,
      body: SingleChildScrollView(
        physics: const ScrollPhysics(),
        child: Column(
          crossAxisAlignment: CrossAxisAlignment.start,
          children: [

            // Carousel Slider
            CarouselSlider(
                items: List.generate(colors.length, (index) => Container(
                  width: double.infinity,
                  height: 140,
                  margin: EdgeInsets.symmetric(horizontal: 10, vertical: 10),
                  decoration: BoxDecoration(
                      color: colors[index],
                      image: DecorationImage(
                          fit: BoxFit.cover,
                          image: NetworkImage("${images[index]}")),
                      borderRadius: BorderRadius.circular(14)
                  ),
                )),
                options: CarouselOptions(
                  autoPlay: true,
                  scrollDirection: Axis.horizontal,
                  scrollPhysics: ScrollPhysics(),
                  autoPlayInterval: Duration(milliseconds: 2000),
                  viewportFraction: 1.0,
                )),

            Container(
                margin: EdgeInsets.only(left: 14,top: 14),
                child: Text("Top Products",style: TextStyle(fontWeight: FontWeight.w800, fontSize: 18),)),


            const SizedBox(
              height: 20,
            ),

            Container(
              width: double.infinity,
              height: 200,
              child: Expanded(
                child: ListView.builder(
                  itemCount: 5,
                  shrinkWrap: true,
                  scrollDirection: Axis.horizontal,
                  physics: const ScrollPhysics(),
                  itemBuilder: (context, index) {
                    return  ProductContainer(
                      productImage: "data:image/jpeg;base64,",
                      productName: "T-Shirt",
                      productPrice: "\$13.45",
                    );
                  },),
              ),
            ),

            Container(
                margin: EdgeInsets.only(left: 14,top: 14),
                child: Text("All Products",style: TextStyle(fontWeight: FontWeight.w800, fontSize: 18),)),

            const SizedBox(
              height: 20,
            ),

            GridView.count(
              crossAxisCount: 2,
              shrinkWrap: true,
              scrollDirection: Axis.vertical,
              physics: const ScrollPhysics(),
              crossAxisSpacing: 10,
              mainAxisSpacing: 10,
              childAspectRatio: 150/180,
              children: List.generate(10, (index) => ProductContainer(
                productImage: "data:image/jpeg;base64,",
                productName: "T-Shirt",
                productPrice: "\$13.45",
              )),
            )

          ],
        ),
      ),
    );
  }
}

class ProductContainer extends StatelessWidget {
  const ProductContainer({
    super.key,
    required this.productImage,
    required this.productName,
    required this.productPrice,
  });

  final String productImage;
  final String productName;
  final String productPrice;

  @override
  Widget build(BuildContext context) {
    return Container(
      width: 150,
      height: 180,
      margin: const EdgeInsets.symmetric(horizontal: 10),
      alignment: Alignment.bottomLeft,
      decoration: BoxDecoration(
          color: Colors.grey.shade400,
          borderRadius: BorderRadius.circular(14),
          image: DecorationImage(
              fit: BoxFit.cover,
              colorFilter: ColorFilter.mode(Colors.black.withOpacity(0.3), BlendMode.darken),
              image: NetworkImage(productImage))
      ),
      child: Container(
          margin: EdgeInsets.only(bottom: 14,left: 8),
          child: Column(
            crossAxisAlignment: CrossAxisAlignment.start,
            mainAxisAlignment: MainAxisAlignment.end,
            children: [
              Text(productName , style: TextStyle(fontWeight: FontWeight.w400, fontSize: 14,color: Colors.white),),
              Text(productPrice , style: TextStyle(fontWeight: FontWeight.w600, fontSize: 12, color: Colors.white),),
            ],
          )),
    );
  }
}

